import re
def test_usingkeywords(keywords, code): ''' validate whether the keywords are used in the code ''' result = True try: for keyword in keywords: regexp = re.compile(r'\b' + keyword + r'\b') matchObj = regexp.search(code) if not matchObj: result = False break except ValueError: result = False return result
def test_mod1(expect, ans): ''' check the mod1 expect: it is not used in the code ans: the code block learner submits ''' keywords = ('print','input','in') return test_usingkeywords(keywords,ans)
